        <NavBarLink href="#story" isactive={path === "#story"} > Story </NavBarLink>
        <NavBarLink href="#moral" isactive={path === "#moral"} > Moral </NavBarLink>

        {/* <li><a href="#story">Story</a></li>
        <li><a href="#moral">Moral</a></li> */}

      </ul>
      <div style={{ height: 64, width: "100%" }}></div>

    </div>
  )
}















